script:
frappe.ui.form.on("Sales Order Item", "item_point_value", function(frm, cdt, cdn) {
    var d = locals[cdt][cdn];
    d.total_point_value=d.item_point_value*d.qty
    refresh_field('items');
});
frappe.ui.form.on("Sales Order Item", "qty", function(frm, cdt, cdn) {
    var d = locals[cdt][cdn];
    d.total_point_value=d.item_point_value*d.qty
    refresh_field('items');
});
frappe.ui.form.on("Sales Order", "validate", function(frm) {
   var grand_total_point_value = 0;
   if(frm.doc.items){
   for(i=0;i<frm.doc.items.length;i++){
       grand_total_point_value= grand_total_point_value +frm.doc.items[i].total_point_value;

    }} 
    frm.doc.grand_total_point_value =grand_total_point_value;

});
